svg.script:
document.getElementById("line1").style.transform = "translateY(-9px)";
document.getElementById("line1").style.transition = ".2s all";
document.getElementById("line2").style.transition = ".2s all";
document.getElementById("line1").style.transformOrigin = "center";
document.getElementById("line3").style.transform = "translateY(9px)";
document.getElementById("line3").style.transition = ".2s all";
document.getElementById("line3").style.transformOrigin = "center";

function openBurger(svg) {
  console.log(svg.dataset.open);
  if (svg.dataset.open === "true") {
    document.getElementById("line2").style.opacity = "0";
    document.getElementById("line1").style.transform = "translate(0, 0)";
    document.getElementById("line3").style.transform = "translate(0, 0)";

    setTimeout(() => {
      document.getElementById("line1").style.transform = "translate(0, 0) rotate(45deg)";
      document.getElementById("line3").style.transform = "translate(1px, 0) rotate(-45deg)";
    }, 200);
    svg.dataset.open = "false";
  } else {
    document.getElementById("line2").style.opacity = "1";
    document.getElementById("line1").style.transform = "translate(0, 0)";
    document.getElementById("line3").style.transform = "translate(0, 0)";

    setTimeout(() => {
      document.getElementById("line1").style.transform = "translateY(-9px)";
      document.getElementById("line3").style.transform = "translateY(9px)";
    }, 200);
    svg.dataset.open = "true";
  }
}
